INSERT [dbo].[Calendars] ([Id], [SourceId], [Page], [EventDate], [Comments], [Quote], [Hide]) VALUES (251, 36, N'248', N'1914-04-03', N'"Under such conditions whatever is evil in men''s natures comes to the front." A porter, Julio, kills another porter and runs into the jungle.', 0, 0)
