INSERT [dbo].[Calendars] ([Id], [SourceId], [Page], [EventDate], [Comments], [Quote], [Hide]) VALUES (253, 36, N'255', N'1914-04-07', N'" The huge catfish which the men had caught was over three feet and a half long…[which] contained the nearly digested remains of a monkey. "', 0, 0)
