INSERT [dbo].[Calendars] ([Id], [SourceId], [Page], [EventDate], [Comments], [Quote], [Hide]) VALUES (255, 36, N'259', N'1914-04-15', N'"We had come over three hundred kilometers in forty-eight days, over absolutely unknown ground; we had seen no human being, although we had twice heard Indians."', 0, 0)
